import"./hoisted.Oozc_hRb.js";const s=document.getElementById("people-form"),l=document.getElementById("people-first-name"),m=document.getElementById("people-last-name"),a=document.getElementById("people-email"),p=document.getElementById("people-business-name"),d=document.getElementById("people-subject"),i=document.getElementById("people-message"),o=document.getElementById("lfw-submit-btn");s.addEventListener("submit",async n=>{n.preventDefault(),o.disabled=!0;try{const e=await fetch("https://emails-forwarding.onrender.com/dts",{method:"POST",headers:{"Content-Type":"application/json"},credentials:"include",body:JSON.stringify({form:"Looking For People",subject:d?.value,firstName:l.value,lastName:m.value,email:a.value,businessName:p.value,message:i.value})}),t=await e.json();e.ok||t?.message?alert(t?.message):alert("Form could not be submitted. Something went wrong")}catch{alert("Form could not be submitted. Something went wrong")}o.disabled=!1,s.reset()});
